import { useRef } from "react";
import { useNavigate } from "react-router-dom";

const USerLogin = () => {

    let navigate = useNavigate

    let email=useRef()
    let password=useRef()

    let loginSubmit=(e)=>{
        let user={
            email:"xyz@gmail.com",
            password:67890
        }
        if (email.current.value == user.email && password.current.value == user.password) {
            //navigate  to admin portal
          
        }
        else{
            alert("Invalid User Credencials")
        }
            // console.log(username.current.value)
            // console.log(password.current.value)
       e.preventDefault()

    }

    return ( 
        <div className="userLogin">
            <h1>User Login</h1>
            <div className="userForm">
            <form onSubmit={loginSubmit} >

                <div className="userEmail">
                     <input ref={email} type="email" placeholder="Enter your email address"/>
                </div>

                <div className="userPassword">
                    <input ref={password} type="password" placeholder="Enter your password" />
                </div>

                <div className="loginButton">
                    <button>Login</button>
                </div>

                </form>
            </div>
        </div>
     );
}
 
export default USerLogin;
