import { useRef } from "react";
impo

const AddUsers = () => {
    let firstName=useRef()
    let lastName=useRef()
    let email=useRef()
    let about=useRef()
    let mobile=useRef()
    let image=useRef()

    let addUser=(e)=>{
        e.preventDefault()

        let user={
            firstName:firstName.current.value,
            lastName:lastName.current.value,
            email:email.current.value,
            mobile:mobile.current.value,
            about:about.current.value,
            image:image.current.value
        
        }
        fetch(" http://localhost:4000/users",{
            method : "POST",
            headers:{'Content-Type':'application/json'},
            body:JSON.stringify(user)
        })
        alert("User Added Successfully...!")
    }

    return ( 
        <div className="addUsers">
            <h1>Add Users</h1>
            <form onSubmit={addUser}>
                <label>First Name :</label>
                <input ref={firstName} type="text" placeholder="Enter First Name"/><br />
                <label >Last Name  :</label>
               <input ref={lastName} type="text" placeholder="Enter Last Name" /><br />
                <label >Email :</label>
               <input ref={email} type="text" placeholder="Enter Email"/><br />
                <label >Image :</label>
              <input ref={image} type="url" placeholder="Enter image Address"/><br />
                <label >Mobile Name:</label>
                <input ref={mobile} type="text" placeholder="Enter mobile number" /><br />
                <br />
                <button>Add User Details</button>

            </form>

        </div>
     );
}
 
export default AddUsers;

