import { useEffect,useState } from "react";
import { useParams } from "react-router-dom";

const ReadBook = () => {
    let params=useParams()
    let book_id = params.id
    // console.log(params)
    let [book,setBook]=useState([])
    useEffect(()=>{
        let fetchData=async()=>{
            let response= await fetch(`http://localhost:4000/books/${book_id}`)
            let data=await response.json();
            setBook(data)
        }
        fetchData()
    })
    return ( 
        <div className="readbook">
            <h1>Book Description</h1>

            <div className="books_list">
                {
                    book.map((info)=>{
                        return <div className="bookdata">
                            <div className="booking">
                                <img src={info.thumbnailUrl} alt="" />
                            </div>
                            <div className="databook">
                                <h2>{info.title}</h2>
                                <h4>{info.shortDescription}</h4>
                                <h4>{info.categories}</h4>
                                <h4>{info.pageCount}</h4>
                                <button onClick={()=>readBook(info.id)}>Read book</button>
                                <button>Delete Book</button>
                            </div>
                        </div>
                    })
                }

            </div>

            
        </div>
     );
}
 
export default ReadBook;
