import { Link } from "react-router-dom";
import "../Styles/navbar.css"
import { useLocation } from "react-router-dom";


const Navbar = () => {
    let location=useLocation()
    console.log(location)
    return (
        <div className="navbar">
            <div className="book">
                <img src="https://www.greenwichlibrary.org/wp-content/themes/greenwich-sage/assets/images/icons/main-logo-green.svg" alt="" />
            </div>
            <div className="logo">
                <h1>Book Hub</h1>
            </div>
            <div className="links">
                {location.pathname =='/adminPortal' ? <Link to="/adminPortal/" >Home</Link> : <Link to="/userPortal/" >Home</Link>}
                {location.pathname =='/adminPortal' ? <Link to="/adminPortal/books" >Books</Link> : <Link to="/userPortal/books" >Books</Link>}
                {location.pathname == '/adminPortal' || <Link to="/adminPortal/users" >Users</Link>} 
                {location.pathname == '/adminPortal' &&  <Link to="/adminPortal/addBooks" >Add Books</Link>} 
                {location.pathname == '/adminPortal' && <Link to="/adminPortal/addUsers" >Add Users</Link>}
                {location.pathname == '/adminPortal' && <Link to="/adminPortal/users" >Users</Link>} 
                {location.pathname == '/adminPortal' &&  <Link to="/adminPortal/addBooks" >Add Books</Link>} 
                {location.pathname == '/adminPortal' && <Link to="/adminPortal/addUsers" >Add Users</Link>}
                {location.pathname =='/adminPortal' ? <Link to="/adminLogin">Logout</Link> : <Link to="/userLogin">Logout</Link>}
            </div>
        </div>
      );
}
 
export default Navbar;
