import { useRef } from "react";

const AdminLogin = () => {
    let email=useRef()
    let password= useRef()

let adminSubmit=(e)=>{
    let admin={
        email:"abc@gmail.com",
        password:12345
    }
    if (email.current.value ==admin.email && password.current.value==admin.password) {
        //navigate  to admin home
        <Link to="">hello</Link>
    }
    else{
        alert("Invalid Admin Credencials")
    }


//    console.log(username.current.value)
//    console.log(password.current.value)
   e.preventDefault()
}
    return ( 
        <div className="adminlogin">
            <h1>Admin Login</h1>
            <div className="adminForm">
                <form onSubmit={adminSubmit} >
                    <div className="adminEmail">
                        <input ref={email} type="email" placeholder="Enter your email address"/>
                    </div>
                    <div className="adminPassword">
                        <input ref={password} type="password" placeholder="Enter your password" />
                    </div>
                    <div className="loginButton">
                        <button>Login</button>
                    </div>
                </form>
            </div>
        </div>
     );
}
 
export default AdminLogin;
